&lt;pre&gt;&lt;code&gt;//java program
public static void heapsort( Comparable []a) &amp;#123;
    for( int i = a.length / 2; i &amp;gt;= 0; i-- )
        percDown( a, i, a.length );
    for( int i = a.length – 1; i &amp;gt; 0; i-- ) &amp;#123;
        swapReferences( a, 0, i );
        percDown( a, 0, i);
    &amp;#125;
&amp;#125;
private static int leftChild( int i ) &amp;#123;   
    return 2 * i + 1;
&amp;#125;
private static void percDown( Comparable [ ] a, int  i,  int n ) &amp;#123;
    int child;
    Comparable tmp;
    for( tmp = a[i];leftChild(i) &amp;lt; n ; i = child ) &amp;#123;
        child = leftchild( i );
        if( child!=n – 1&amp;amp;&amp;amp; a[child].compareTo( a[ child + 1 ] ) &amp;lt; 0 )
            child ++;
        if( tmp . compareTo( a[ child ] &amp;lt; 0 )
            a[ i ] = a[ child ];
        else
            break;
    &amp;#125;
    a[i] = tmp;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3 id=&#34;434-堆排序算法复杂度分析&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#434-堆排序算法复杂度分析&#34;&gt;#&lt;/a&gt; 4.3.4. 堆排序算法复杂度分析&lt;/h3&gt;
&lt;p&gt;&lt;a href=&#34;https://spricoder.oss-cn-shanghai.aliyuncs.com/2019-Data-Structure/img/cpt11/18.png&#34;&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/18.png&#34; alt=&#34;img&#34; /&gt;&lt;/a&gt;&lt;/p&gt;
&lt;h1 id=&#34;7-归并排序&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#7-归并排序&#34;&gt;#&lt;/a&gt; 7. 归并排序&lt;/h1&gt;
&lt;ol&gt;
&lt;li&gt;归并：两个 (多个) 有序的文件组合成一个有序文件 方法：每次取出两个序列中的小的元素输出之；当一序列完，则输出另一序列的剩余部分&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;&lt;a href=&#34;https://spricoder.oss-cn-shanghai.aliyuncs.com/2019-Data-Structure/img/cpt11/14.png&#34;&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/14.png&#34; alt=&#34;img&#34; /&gt;&lt;/a&gt;&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;算法思想：分而治之 (分治思想)&lt;/li&gt;
&lt;/ol&gt;
&lt;h2 id=&#34;71-代码实现&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#71-代码实现&#34;&gt;#&lt;/a&gt; 7.1. 代码实现&lt;/h2&gt;
&lt;pre&gt;&lt;code&gt;//c++
template&amp;lt;class Type&amp;gt; void merge(datalist&amp;lt;Type&amp;gt; &amp;amp; initList, datalist&amp;lt;Type&amp;gt;&amp;amp; mergedList, const int l, const int m, const int n) &amp;#123;
    int i=l, j=m+1, k= l;
    while ( i&amp;lt;=m &amp;amp;&amp;amp; j&amp;lt;=n ) if (initList.Vector[i].getkey( )&amp;lt;initList.Vector[j].getkey( )) &amp;#123;
        mergedList.Vector[k]=initList.Vector[i];
        i++;
        k++;
    &amp;#125;
    else&amp;#123;
        mergedList.Vector[k]=initList.Vector[j];
        j++;
        k++;
    &amp;#125;
    if (i&amp;lt;=m)
        for (int n1=k, n2=i; n1&amp;lt;=n &amp;amp;&amp;amp; n2&amp;lt;=m;  n1++, n2++)
            mergedList.Vector[n1]=initList.Vector[n2];
    else
        for(int n1=k, n2=j; n1&amp;lt;=n &amp;amp;&amp;amp; n2&amp;lt;=n; n1++, n2++)
            mergedList.Vector[n1]=initList.Vector[n2];
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;72-归并排序&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#72-归并排序&#34;&gt;#&lt;/a&gt; 7.2. 归并排序&lt;/h2&gt;
&lt;ol&gt;
&lt;li&gt;方法
&lt;ul&gt;
&lt;li&gt;n 个长为 1 的对象两两合并，得 n/2 个长为 2 的文件&lt;/li&gt;
&lt;li&gt;n/2 个长为 2…………………. 得 n/4 个长为 4 的文件…&lt;/li&gt;
&lt;li&gt;2 个长为 n/2 的对象两两合并，得 1 个长为 n 的文件&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;h3 id=&#34;721-例子&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#721-例子&#34;&gt;#&lt;/a&gt; 7.2.1. 例子&lt;/h3&gt;
&lt;p&gt;&lt;a href=&#34;https://spricoder.oss-cn-shanghai.aliyuncs.com/2019-Data-Structure/img/cpt11/19.png&#34;&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/19.png&#34; alt=&#34;img&#34; /&gt;&lt;/a&gt;&lt;/p&gt;
&lt;h3 id=&#34;722-非递归算法c代码实现&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#722-非递归算法c代码实现&#34;&gt;#&lt;/a&gt; 7.2.2. 非递归算法 c++ 代码实现&lt;/h3&gt;
&lt;pre&gt;&lt;code&gt;//c++
template &amp;lt;class Type&amp;gt; void MergeSort(datalist &amp;lt;Type&amp;gt; &amp;amp; list) &amp;#123;
    datalist &amp;lt;Type&amp;gt; tempList(list.MaxSize); 
    int len=1;
    while (len&amp;lt;list.CurrentSize) &amp;#123;
        MergePass(list, tempList, len); len *=2 ;
        if (len &amp;gt;= list.CurrentSize) &amp;#123;
            for (int i=0;i&amp;lt; list.CurrentSize; i++)
                list.Vector[i]=tempList.Vector[i];
        &amp;#125;else&amp;#123;
            MergePass(tempList, list, len); len*=2;
        &amp;#125;
    &amp;#125;
    delete[]tempList;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;ol&gt;
&lt;li&gt;当两段均满 len 长时调用 merge&lt;/li&gt;
&lt;li&gt;当一长一短时也调用 merge (但第二段的参数不同)&lt;/li&gt;
&lt;li&gt;当只有一段时，则复抄&lt;/li&gt;
&lt;li&gt;块合并算法实现&lt;/li&gt;
&lt;/ol&gt;
&lt;pre&gt;&lt;code&gt;//c++
template &amp;lt;class Type&amp;gt; void MergePass( datalist&amp;lt;Type&amp;gt; &amp;amp; initList, datalist &amp;lt;Type&amp;gt; &amp;amp; mergedList, const int len) &amp;#123;
    int i=0;
    while (i+2*len&amp;lt;=initList.CurrentSize-1) &amp;#123;
        merge( initList, mergedList, i, i+len-1, i+2*len-1);
        i+=2*len;
    &amp;#125;
    if(i+len &amp;lt;= initList.CurrentSize-1)
        merge(initList, mergedList, i, i+len-1,initList.CurrentSize-1);//因为有可能有块长度为余数，并不满足结果的，所以要额外处理
    else
        for( int j=i ; j&amp;lt;= initList.CurrentSize; j++)
            mergedList.Vector[j]=initList.Vector[j];
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;ol&gt;
&lt;li&gt;算法分析：合并趟数 log2n, 每趟比较 n 次，所以为 O (nlog2n)&lt;/li&gt;
&lt;li&gt;稳定性：稳定。&lt;/li&gt;
&lt;/ol&gt;
&lt;h3 id=&#34;723-递归算法java实现&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#723-递归算法java实现&#34;&gt;#&lt;/a&gt; 7.2.3. 递归算法 java 实现&lt;/h3&gt;
&lt;pre&gt;&lt;code&gt;//java
public static void mergeSort( Comparable [ ] a ) &amp;#123;
    Comparable [ ] tmpArray = new Comparable[a.length];
    mergeSort( a, tmpArray, 0, a.length – 1 );
&amp;#125;                            
private static void mergeSort( Comparable [ ] a, Comparable [] tmpArray, int left, int right ) &amp;#123;
    if( left &amp;lt; right ) &amp;#123;
        int center = ( left + right ) / 2;
        mergeSort(a, tmparray, left, center );
        mergeSort(a, tmpArray, center + 1, right );
        merge( a, tmpArray, left, center + 1, right );
    &amp;#125;
&amp;#125;
private static void merge( Comparable [ ] a, Comparable [] tmpArray, int leftPos, int rightPos, int rightEnd ) &amp;#123;
    int leftEnd = rightPos – 1;
    int tmpPos = leftPos;
    int numElements = rightEnd – leftPos + 1;
    while( leftPos &amp;lt;= leftEnd &amp;amp;&amp;amp; rightPos &amp;lt;= rightEnd )
        if( a[ leftPos ].compareTo( a[ rightPos ] ) &amp;lt;= 0 )
            tmpArray[ tmpPos++ ] = a[ leftPos++ ];
        else
            tmpArray[ tmpPos++ ] = a[ rightPos++ ];
    while( leftPos &amp;lt;= leftEnd )
        tmpArray[ tmpPos++ ] = a[ leftPos++ ];
    while( rightpos &amp;lt;= rightEnd)
        tmpArray[ tmpPos++] = a[ rightpos++ ];
    for( int i = 0; i &amp;lt; numElements; i++, rightEnd-- )
        a[ rightEnd ] = tmpArray[ rightEnd ];
&amp;#125;     
&lt;/code&gt;&lt;/pre&gt;
&lt;h3 id=&#34;724-算法分析&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#724-算法分析&#34;&gt;#&lt;/a&gt; 7.2.4. 算法分析&lt;/h3&gt;
&lt;ol&gt;
&lt;li&gt;合并趟数 log2n, 每趟比较 n 次，所以为 O (nlog2n)&lt;/li&gt;
&lt;/ol&gt;
