rable [] a ) &amp;#123;    for (int gap = a.length/2 ; gap&gt;0 ; gap/=2 )        for (int i = gap; i &amp;lt; a.length; i++) &amp;#123;            // 遍历一遍            Comparable tmp = a [i];            int j = i;            for (;j &gt;= gap &amp;amp;&amp;amp; tmp.compareTo ( a [j-gap] )&amp;lt; 0;j -= gap )                // 完成一遍下滤                a [j] = a [j – gap];            a [j] = tmp;    &amp;#125;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;h3 id=&#34;232-希尔排序的稳定性&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#232-希尔排序的稳定性&#34;&gt;#&lt;/a&gt; 2.3.2. 希尔排序的稳定性&lt;/h3&gt;
&lt;p&gt;&lt;strong&gt;不稳定&lt;/strong&gt;的&lt;/p&gt;
&lt;h3 id=&#34;233-希尔排序算法分析&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#233-希尔排序算法分析&#34;&gt;#&lt;/a&gt; 2.3.3. 希尔排序算法分析&lt;/h3&gt;
&lt;ol&gt;
&lt;li&gt;与选择的缩小增量有关，但到目前还不知如何选择最好结果的缩小增量序列。&lt;/li&gt;
&lt;li&gt;平均比较次数与移动次数大约 n1.3 左右&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/image-20230216095012176.png&#34; alt=&#34;image-20230216095012176&#34; /&gt;&lt;/p&gt;
&lt;h1 id=&#34;3-交换排序一类排序算法&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#3-交换排序一类排序算法&#34;&gt;#&lt;/a&gt; 3. 交换排序 (一类排序算法)&lt;/h1&gt;
&lt;ol&gt;
&lt;li&gt;方法的本质：不断的交换反序的对偶，直到不再有反序的对偶为止。&lt;/li&gt;
&lt;li&gt;两种方法：
&lt;ol&gt;
&lt;li&gt;冒泡排序 (Bubble sort)&lt;/li&gt;
&lt;li&gt;快速排序 (Quick sort)&lt;/li&gt;
&lt;/ol&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;h2 id=&#34;31-冒泡排序注意是优化过的&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#31-冒泡排序注意是优化过的&#34;&gt;#&lt;/a&gt; 3.1. 冒泡排序（注意是优化过的）&lt;/h2&gt;
&lt;p&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/image-20230216100539527.png&#34; alt=&#34;image-20230216100539527&#34; /&gt;&lt;/p&gt;
&lt;h3 id=&#34;311-源码&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#311-源码&#34;&gt;#&lt;/a&gt; 3.1.1. 源码&lt;/h3&gt;
&lt;p&gt;&lt;img data-src=&#34;https://quasdo.oss-cn-hangzhou.aliyuncs.com/img/image-20230216100532018.png&#34; alt=&#34;image-20230216100532018&#34; /&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;//java
public static void Bubble( int [ ] a , int n) &amp;#123;
    //Bubble largest element in a[0:n-1] to right 
    for(int i=0; i&amp;lt;n-1; i++) 
        if(a[i] &amp;gt; a[i+1])
            swap(a[i],a[i+1]);
    &amp;#125;
public static void BubbleSort( int [ ] a, int n) &amp;#123;  
    //Sort a[0:n-1] using a bubble sort 
    for(int i=n ;i&amp;gt;1; i--) 
        Bubble(a,i); 
&amp;#125;

//C++template&amp;lt;class Type&amp;gt; void  BubbleSort( datalist&amp;lt;Type&amp;gt; &amp;amp; list) &amp;#123;    int pass=1;    int exchange=1;    while (pass&amp;lt;list.CurrentSize &amp;amp;&amp;amp;exchange) &amp;#123;        BubbleExchange(list, pass, exchange);        pass++;    &amp;#125;&amp;#125;template&amp;lt;class Type&amp;gt;  void  BubbleExchange(datalist&amp;lt;Type&amp;gt; &amp;amp;list, const int i, int &amp;amp; exchange) &amp;#123;    exchange=0;    for (int j=list.CurrentSize-1; j&amp;gt;=i; j--)        if (list.Vector[j-1].getkey()&amp;gt;list.Vector[j].getkey()) &amp;#123;            swap(list.Vector[j-1],  list.Vector[j]);            exchange=1;        &amp;#125;&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;h3 id=&#34;312-冒泡排序算法分析&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#312-冒泡排序算法分析&#34;&gt;#&lt;/a&gt; 3.1.2. 冒泡排序算法分析&lt;/h3&gt;
&lt;ol&gt;
&lt;li&gt;进行几次元素之间的比较？
&lt;ul&gt;
&lt;li&gt;从 n-1 开始往下比较&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;li&gt;进行了几次元素之间的交换
&lt;ul&gt;
&lt;li&gt;不确定&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;h3 id=&#34;313-冒泡排序算法的稳定性&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#313-冒泡排序算法的稳定性&#34;&gt;#&lt;/a&gt; 3.1.3. 冒泡排序算法的稳定性&lt;/h3&gt;
&lt;p&gt;&lt;strong&gt;稳定的&lt;/strong&gt;&lt;/p&gt;
&lt;h3 id=&#34;314-冒泡排序算法复杂度分析&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#314-冒泡排序算法复杂度分析&#34;&gt;#&lt;/a&gt; 3.1.4. 冒泡排序算法复杂度分析&lt;/h3&gt;
&lt;ol&gt;
&lt;li&gt;最小比较次数
&lt;ul&gt;
&lt;li&gt;有序：n-1 次比较，移动次数为 0&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;li&gt;最大比较次数
&lt;ul&gt;
&lt;li&gt;逆序：(n-1)+(n-2)+…+1=n (n-1)/2 约等于 O (n2) (比较次数)&lt;br /&gt;
 移动次数 = 3*(1+2+3+…+n)=(3/2) n (n-1) i=1 (移动次数)&lt;/li&gt;
&lt;/ul&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;h2 id=&#34;32-快速排序&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#32-快速排序&#34;&gt;#&lt;/a&gt; 3.2. 快速排序&lt;/h2&gt;
&lt;h3 id=&#34;321-算法内容&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#321-算法内容&#34;&gt;#&lt;/a&gt; 3.2.1. 算法内容&lt;/h3&gt;
&lt;ol&gt;
&lt;li&gt;在 n 个对象中，取一个对象 (如第一个对象 —— 基准 pivot)，按该对象的关键码
&lt;ol&gt;
&lt;li&gt;把所有&lt;strong&gt;小于等于&lt;/strong&gt;该关键码的对象分划在它的左边。&lt;/li&gt;
&lt;li&gt;&lt;strong&gt;大于&lt;/strong&gt;该关键码的对象分划在它的右边。&lt;/li&gt;
&lt;/ol&gt;
&lt;/li&gt;
&lt;li&gt;对左边和右边 (子序列) 分别再用快速排序。&lt;/li&gt;
&lt;/ol&gt;
